import React, { useState } from "react";

export default function AdminRegister() {
  const [name, setName] = useState("");
  const [gmail, setGmail] = useState("");
  const [password, setPassword] = useState("");
  const [phone, setPhone] = useState("");
  const [nameTouched, setNameTouched] = useState(false);
  const [gmailTouched, setGmailTouched] = useState(false);
  const [passwordTouched, setPasswordTouched] = useState(false);
  const [phoneTouched, setPhoneTouched] = useState(false);

  const isNameValid = /^[A-Za-z\s]+$/.test(name) && name.length >= 3;
  const isGmailValid = gmail.includes("@gmail.com");
  const isPasswordValid = password.length > 4;
  const isPhoneValid = /^\d{10}$/.test(phone);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!isNameValid || !isGmailValid || !isPasswordValid || !isPhoneValid) {
      alert("Please fill out all fields correctly.");
      return;
    }

    // Post data to the server
    try {
      const response = await fetch("http://localhost:5000/adminregister", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name, gmail, password, phone }),
      });

      const data = await response.json();

      if (response.ok) {
        alert("Admin registered successfully!");
        console.log(data);
        // Clear form fields
        setName("");
        setGmail("");
        setPassword("");
        setPhone("");
      } else {
        alert(data.error || "Failed to register admin.");
      }
    } catch (error) {
      console.error("Error posting data:", error);
      alert("An error occurred. Please try again.");
    }
  };

  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "100vh",
        background: "linear-gradient(135deg, #2c3e50, #4ca1af)",
        padding: "0 50px",
      }}
    >
      <div
        style={{
          width: "350px",
          height: "auto",
          background: "rgba(255, 255, 255, 0.1)",
          borderRadius: "10px",
          boxShadow: "0 4px 10px rgba(0, 0, 0, 0.2)",
          padding: "30px",
          backdropFilter: "blur(10px)",
        }}
      >
        <h1 style={{ color: "#fff", marginBottom: "20px" }}>Admin Register</h1>
        <form onSubmit={handleSubmit}>
          <div style={{ marginBottom: "15px" }}>
            <label style={{ color: "#fff", display: "block", marginBottom: "5px" }}>Name</label>
            <input
              type="text"
              placeholder="Enter your name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              onFocus={() => setNameTouched(true)}
              style={{
                width: "100%",
                padding: "10px",
                borderRadius: "5px",
                border: "none",
                outline: "none",
                background: "rgba(255, 255, 255, 0.3)",
                color: "#fff",
              }}
            />
            {nameTouched && !isNameValid && (
              <span style={{ color: "red", fontSize: "12px" }}>
                Enter a valid name (only letters and spaces, at least 3 characters)
              </span>
            )}
          </div>

          <div style={{ marginBottom: "15px" }}>
            <label style={{ color: "#fff", display: "block", marginBottom: "5px" }}>Phone</label>
            <input
              type="text"
              placeholder="Enter your phone number"
              value={phone}
              onChange={(e) => setPhone(e.target.value)}
              onFocus={() => setPhoneTouched(true)}
              style={{
                width: "100%",
                padding: "10px",
                borderRadius: "5px",
                border: "none",
                outline: "none",
                background: "rgba(255, 255, 255, 0.3)",
                color: "#fff",
              }}
            />
            {phoneTouched && !isPhoneValid && (
              <span style={{ color: "red", fontSize: "12px" }}>
                Enter a valid phone number (exactly 10 digits)
              </span>
            )}
          </div>

          <div style={{ marginBottom: "15px" }}>
            <label style={{ color: "#fff", display: "block", marginBottom: "5px" }}>Gmail</label>
            <input
              type="text"
              placeholder="Enter your Gmail"
              value={gmail}
              onChange={(e) => setGmail(e.target.value)}
              onFocus={() => setGmailTouched(true)}
              style={{
                width: "100%",
                padding: "10px",
                borderRadius: "5px",
                border: "none",
                outline: "none",
                background: "rgba(255, 255, 255, 0.3)",
                color: "#fff",
              }}
            />
            {gmailTouched && !isGmailValid && (
              <span style={{ color: "red", fontSize: "12px" }}>
                Enter a valid Gmail (must include @gmail.com)
              </span>
            )}
          </div>

          <div style={{ marginBottom: "15px" }}>
            <label style={{ color: "#fff", display: "block", marginBottom: "5px" }}>Password</label>
            <input
              type="password"
              placeholder="Enter your password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              onFocus={() => setPasswordTouched(true)}
              style={{
                width: "100%",
                padding: "10px",
                borderRadius: "5px",
                border: "none",
                outline: "none",
                background: "rgba(255, 255, 255, 0.3)",
                color: "#fff",
              }}
            />
            {passwordTouched && !isPasswordValid && (
              <span style={{ color: "red", fontSize: "12px" }}>
                Enter a valid password (at least 5 characters)
              </span>
            )}
          </div>

          <button
            type="submit"
            disabled={!isNameValid || !isGmailValid || !isPasswordValid || !isPhoneValid}
            style={{
              width: "100%",
              padding: "10px",
              borderRadius: "5px",
              border: "none",
              background: "#007BFF",
              color: "#fff",
              fontWeight: "bold",
              cursor: "pointer",
            }}
          >
            Submit
          </button>
        </form>
      </div>
    </div>
  );
}
