import React, { useState } from "react";

export default function Adminhome() {
  const [image, setImage] = useState(null);
  const [price, setPrice] = useState("");

  const handleChange = (e) => {
    const { type, value, files } = e.target;
    type === "file" ? setImage(URL.createObjectURL(files[0])) : setPrice(value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!image || !price) return alert("Please fill all fields.");
    alert(`Image: ${image}\nPrice: ${price}\nThe product is added to cart.`);
    setImage(null);
    setPrice("");
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="file" accept="image/*" onChange={handleChange} />
      {image && <img src={image} alt="Selected" style={{ width: 100, height: 100, marginTop: 10 }} />}
      <input type="number" value={price} onChange={handleChange} placeholder="Enter price" />
      <button type="submit" disabled={!price || !image}>Submit</button>
    </form>
  );
}
