import React, { useState, useEffect } from "react";
import axios from "axios";

export default function Adminhome() {
  const [image, setImage] = useState(null);
  const [price, setPrice] = useState("");
  const [productName, setProductName] = useState("");
  const [productId, setProductId] = useState("");
  const [products, setProducts] = useState([]);
  const [editing, setEditing] = useState(null); // Track currently edited product

  useEffect(() => {
    const fetchProductId = async () => {
      try {
        const response = await axios.get("http://localhost:5000/nextProductId");
        setProductId(response.data.productId);
      } catch (error) {
        console.error("Error fetching product ID:", error);
        alert("Failed to fetch product ID");
      }
    };

    fetchProductId();

    const fetchProducts = async () => {
      try {
        const response = await axios.get("http://localhost:5000/products");
        setProducts(response.data);
      } catch (error) {
        console.error("Error fetching products:", error);
        alert("Failed to fetch products");
      }
    };

    fetchProducts();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault(); // Prevent page reload on form submission
    try {
      const formData = new FormData();
      formData.append("productId", productId);
      formData.append("productName", productName);
      formData.append("price", price);
      if (image) formData.append("image", image);

      await axios.post("http://localhost:5000/products", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      alert("Product added successfully!");
      setProductId(""); // Clear form fields
      setProductName("");
      setPrice("");
      setImage(null);

      // Fetch updated product list
      const response = await axios.get("http://localhost:5000/products");
      setProducts(response.data);
    } catch (error) {
      console.error("Error adding product:", error);
      alert("Failed to add product");
    }
  };

  const handleUpdate = async (product) => {
    try {
      const formData = new FormData();
      formData.append("productName", productName || product.productName);
      formData.append("price", price || product.price);
      if (image) formData.append("image", image);

      await axios.put(`http://localhost:5000/products/${product.productId}`, formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      alert("Product updated successfully!");
      setEditing(null);
      const updatedProducts = await axios.get("http://localhost:5000/products");
      setProducts(updatedProducts.data);
    } catch (error) {
      console.error("Error updating product:", error);
      alert("Failed to update product");
    }
  };

  const handleDelete = async (productId) => {
    try {
      await axios.delete(`http://localhost:5000/products/${productId}`);
      alert("Product deleted successfully!");
      setProducts(products.filter((product) => product.productId !== productId));
    } catch (error) {
      console.error("Error deleting product:", error);
      alert("Failed to delete product");
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <div>
          <input
            type="text"
            value={productId}
            readOnly
            placeholder="Product ID"
            style={{ marginBottom: "10px", cursor: "not-allowed", backgroundColor: "#f0f0f0" }}
          />
        </div>
        <div>
          <input
            type="file"
            accept="image/*"
            name="image"
            onChange={(e) => setImage(e.target.files[0])}
          />
        </div>
        <div>
          <input
            type="text"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
            placeholder="Enter price"
          />
        </div>
        <div>
          <input
            type="text"
            value={productName}
            onChange={(e) => setProductName(e.target.value)}
            placeholder="Enter product name"
          />
        </div>
        <button type="submit">Submit</button>
      </form>

      <div style={{ marginTop: "50px" }}>
        <h2>Product List</h2>
        <table border="1" cellPadding="10" cellSpacing="0" style={{ width: "100%", textAlign: "left" }}>
          <thead>
            <tr>
              <th>Product ID</th>
              <th>Product Name</th>
              <th>Price</th>
              <th>Image</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {products.map((product) => (
              <tr key={product.productId}>
                <td>{product.productId}</td>
                <td>
                  {editing === product.productId ? (
                    <input
                      type="text"
                      defaultValue={product.productName}
                      onChange={(e) => setProductName(e.target.value)}
                    />
                  ) : (
                    product.productName
                  )}
                </td>
                <td>
                  {editing === product.productId ? (
                    <input
                      type="text"
                      defaultValue={product.price}
                      onChange={(e) => setPrice(e.target.value)}
                    />
                  ) : (
                    product.price
                  )}
                </td>
                <td>
                  {editing === product.productId ? (
                    <input
                      type="file"
                      accept="image/*"
                      onChange={(e) => setImage(e.target.files[0])}
                    />
                  ) : (
                    <img
                      src={product.imageUrl}
                      alt={product.productName}
                      style={{ width: "100px", height: "100px" }}
                    />
                  )}
                </td>
                <td>
                  {editing === product.productId ? (
                    <button onClick={() => handleUpdate(product)}>Save</button>
                  ) : (
                    <button onClick={() => setEditing(product.productId)}>Edit</button>
                  )}
                  <button onClick={() => handleDelete(product.productId)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
