import React, { useState } from "react";

export default function AdminSigninForm() {
  const [name, setName] = useState("");
  const [gmail, setGmail] = useState("");
  const [password, setPassword] = useState("");
  const [nameTouched, setNameTouched] = useState(false);
  const [gmailTouched, setGmailTouched] = useState(false);
  const [passwordTouched, setPasswordTouched] = useState(false);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  const isNameValid = /^[A-Za-z\s]+$/.test(name) && name.length >= 3;
  const isGmailValid = gmail.includes("@gmail.com");
  const isPasswordValid = password.length > 4;

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!isNameValid || !isGmailValid || !isPasswordValid) {
      alert("Please fill out all fields correctly.");
      return;
    }

    try {
      const response = await fetch("http://localhost:5000/adminsignin", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ name, gmail, password }),
      });

      const result = await response.json();

      if (response.ok) {
        setSuccess(result.message);
        setError("");
        setName("");
        setGmail("");
        setPassword("");
      } else {
        setError(result.message);
        setSuccess("");
      }
    } catch (err) {
      setError("An error occurred. Please try again later.");
      setSuccess("");
    }
  };

  return (
    <div className="signin-container">
      <div className="signin-form">
        <h1>ADMIN SIGNIN</h1>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Name</label>
            <input
              type="text"
              placeholder="Enter your name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              onFocus={() => setNameTouched(true)}
            />
            {nameTouched && !isNameValid && (
              <span className="error">
                Enter a valid name (only letters and spaces, at least 3 characters)
              </span>
            )}
          </div>
          <div className="form-group">
            <label>Gmail</label>
            <input
              type="text"
              placeholder="Enter your Gmail"
              value={gmail}
              onChange={(e) => setGmail(e.target.value)}
              onFocus={() => setGmailTouched(true)}
            />
            {gmailTouched && !isGmailValid && (
              <span className="error">
                Enter a valid Gmail (must include @gmail.com)
              </span>
            )}
          </div>
          <div className="form-group">
            <label>Password</label>
            <input
              type="password"
              placeholder="Enter your password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              onFocus={() => setPasswordTouched(true)}
            />
            {passwordTouched && !isPasswordValid && (
              <span className="error">
                Enter a valid password (at least 5 characters)
              </span>
            )}
          </div>
          <button
            type="submit"
            disabled={!isNameValid || !isGmailValid || !isPasswordValid}
          >
            Submit
          </button>
          {success && <p className="success">{success}</p>}
          {error && <p className="error">{error}</p>}
        </form>
      </div>
    </div>
  );
}
