"use client"

import React ,{ useState } from "react";

export default function Admindelete() {
  const [name, setName] = useState("");
  const [gmail, setGmail] = useState("");
  const [password, setPassword] = useState("");
  const [nameTouched, setNameTouched] = useState(false);
  const [gmailTouched, setGmailTouched] = useState(false);
  const [passwordTouched, setPasswordTouched] = useState(false);
  const [message, setMessage] = useState("");

  const isNameValid = name.length >= 3;
  const isGmailValid = gmail.includes("@gmail.com");
  const isPasswordValid = password.length > 6;

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!isNameValid) {
      alert("name is error");
      return;
    }
    if (!isGmailValid) {
      alert("gmail is error");
      return;
    }
    if (!isPasswordValid) {
      alert("password is error");
      return;
    }

    try {
      const response = await fetch("http://localhost:5000/delete-admin", {
        method: "DELETE",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          name,
          gmail,
          password,
        }),
      });

      const data = await response.json();

      if (response.ok) {
        setMessage(data.message);
        // Clear form after successful deletion
        setName("");
        setGmail("");
        setPassword("");
      } else {
        setMessage(data.error || "Failed to delete admin");
      }
    } catch (error) {
      setMessage("Error connecting to server");
      console.error("Error:", error);
    }
  };

  const nameChange = (e) => {
    setName(e.target.value);
  };
  const gmailChange = (e) => {
    setGmail(e.target.value);
  };
  const passwordChange = (e) => {
    setPassword(e.target.value);
  };

  return (
    <div className="max-w-md mx-auto p-6 bg-white shadow-md rounded-2xl">
      <h1>Admin Delete</h1>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <input
            type="text"
            value={name}
            onChange={nameChange}
            onFocus={() => setNameTouched(true)}
            placeholder="Enter name"
            className="w-full p-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-400"
          />
          {nameTouched && !isNameValid && (
            <span className="text-red-500">Name must be at least 3 characters long.</span>
          )}
        </div>
        <div>
          <input
            type="text"
            value={gmail}
            onChange={gmailChange}
            onFocus={() => setGmailTouched(true)}
            placeholder="Enter gmail"
            className="w-full p-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-400"
          />
          {gmailTouched && !isGmailValid && (
            <span className="text-red-500">Gmail must include "@gmail.com".</span>
          )}
        </div>
        <div>
          <input
            type="password"
            value={password}
            onChange={passwordChange}
            onFocus={() => setPasswordTouched(true)}
            placeholder="Enter password"
            className="w-full p-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-400"
          />
          {passwordTouched && !isPasswordValid && (
            <span className="text-red-500">Password must be more than 6 characters long.</span>
          )}
        </div>
        <button
          type="submit"
          disabled={!isNameValid || !isGmailValid || !isPasswordValid}
          className="w-full p-3 bg-blue-500 text-white rounded-xl hover:bg-blue-600 disabled:bg-gray-300"
        >
          Delete Admin
        </button>
        {message && <div className="mt-4 text-center font-medium">{message}</div>}
      </form>
    </div>
  );
}
