import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Mainpage from "./Mainpage";
import Adminhome from "./Adminhome";

export default function App() {
  const [adminData, setAdminData] = useState([]); // State for data storage

  // Function to handle adding data
  const handleAddData = (newData) => {
    setAdminData((prevData) => [...prevData, newData]);
  };

  // Function to delete data by index
  const handleDeleteData = (index) => {
    setAdminData((prevData) => prevData.filter((_, i) => i !== index));
  };

  return (
    <Router>
      <Routes>
        <Route
          path="/Adminhome"
          element={
            <Adminhome addData={handleAddData} deleteData={handleDeleteData} />
          }
        />
        <Route path="/Mainpage" element={<Mainpage data={adminData} />} />
        <Route path="/" element={<Mainpage data={adminData} />} />
      </Routes>
    </Router>
  );
}
