import React, { useState } from "react";
import PhoneInput from "react-phone-input-2";
import "react-phone-input-2/lib/style.css";

export default function AdminResister() {
  const [name, setName] = useState("");
  const [gmail, setGmail] = useState("");
  const [password, setPassword] = useState("");
  const [phone, setPhone] = useState("");
  const [nameTouched, setNameTouched] = useState(false);
  const [gmailTouched, setGmailTouched] = useState(false);
  const [passwordTouched, setPasswordTouched] = useState(false);
  const [phoneTouched, setPhoneTouched] = useState(false);

  const isNameValid = /^[A-Za-z\s]+$/.test(name) && name.length >= 3;
  const isGmailValid = gmail.includes("@gmail.com");
  const isPasswordValid = password.length > 4;
  const isPhoneValid = phone.length >= 10;

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!isNameValid || !isGmailValid || !isPasswordValid || !isPhoneValid) {
      alert("Please fill out all fields correctly.");
      return;
    }
    alert(`Form submitted successfully!`);
    console.log({ name, gmail, password, phone });
    window.location.reload();
  };

  const nameChange = (e) => {
    setName(e.target.value);
  };

  const gmailChange = (e) => {
    setGmail(e.target.value);
  };

  const passwordChange = (e) => {
    setPassword(e.target.value);
  };

  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "100vh",
        background: "linear-gradient(to right, #434343, #000000)",
        position: "relative",
        overflow: "hidden",
      }}
    >
      <div
        style={{
          position: "absolute",
          top: "50%",
          left: "50%",
          transform: "translate(-50%, -50%)",
          width: "400px",
          padding: "30px",
          background: "rgba(255, 255, 255, 0.1)",
          borderRadius: "15px",
          boxShadow: "0 0 20px rgba(255, 255, 255, 0.2)",
          backdropFilter: "blur(10px)",
          color: "#fff",
        }}
      >
        <h1
          style={{
            textAlign: "center",
            marginBottom: "20px",
            fontSize: "24px",
            color: "#fff",
          }}
        >
          ADMIN REGISTER
        </h1>
        <form onSubmit={handleSubmit}>
          <div style={{ marginBottom: "15px" }}>
            <input
              type="text"
              placeholder="Enter your name"
              value={name}
              onChange={nameChange}
              onFocus={() => setNameTouched(true)}
              style={{
                width: "100%",
                padding: "10px",
                borderRadius: "5px",
                border: "none",
                background: "rgba(255, 255, 255, 0.2)",
                color: "#fff",
                outline: "none",
                fontSize: "14px",
              }}
              // Inline style for placeholder
              placeholder="Enter your name"
              placeholderStyle={{
                color: "black", // Apply black color to placeholder
              }}
            />
            {nameTouched && !isNameValid && (
              <span style={{ color: "red", fontSize: "12px" }}>
                Enter a valid name (only letters and spaces, at least 3
                characters)
              </span>
            )}
          </div>
          <div style={{ marginBottom: "15px" }}>
            <PhoneInput
              country={"in"}
              value={phone}
              onChange={(phone) => setPhone(phone)}
              onFocus={() => setPhoneTouched(true)}
              inputStyle={{
                width: "100%",
                borderRadius: "5px",
                border: "none",
                background: "rgba(255, 255, 255, 0.2)",
                color: "#fff",
                fontSize: "14px",
                outline: "none",
              }}
              inputProps={{
                placeholder: "Enter your phone number",
                style: {
                  color: "black", // Fully black placeholder text
                },
              }}
            />
            {phoneTouched && !isPhoneValid && (
              <span style={{ color: "red", fontSize: "12px" }}>
                Enter the phone number (at least 10 digits)
              </span>
            )}
          </div>
          <div style={{ marginBottom: "15px" }}>
            <input
              type="text"
              placeholder="Enter your Gmail"
              value={gmail}
              onChange={gmailChange}
              onFocus={() => setGmailTouched(true)}
              style={{
                width: "100%",
                padding: "10px",
                borderRadius: "5px",
                border: "none",
                background: "rgba(255, 255, 255, 0.2)",
                color: "#fff",
                outline: "none",
                fontSize: "14px",
              }}
            />
            {gmailTouched && !isGmailValid && (
              <span style={{ color: "red", fontSize: "12px" }}>
                Enter a valid Gmail (must include @gmail.com)
              </span>
            )}
          </div>
          <div style={{ marginBottom: "15px" }}>
            <input
              type="password"
              placeholder="Enter your password"
              value={password}
              onChange={passwordChange}
              onFocus={() => setPasswordTouched(true)}
              autoComplete="off"
              style={{
                width: "100%",
                padding: "10px",
                borderRadius: "5px",
                border: "none",
                background: "rgba(255, 255, 255, 0.2)",
                color: "#fff",
                outline: "none",
                fontSize: "14px",
              }}
            />
            {passwordTouched && !isPasswordValid && (
              <span style={{ color: "red", fontSize: "12px" }}>
                Enter the password (at least 5 characters)
              </span>
            )}
          </div>
          <button
            type="submit"
            disabled={
              !isNameValid || !isGmailValid || !isPasswordValid || !isPhoneValid
            }
            style={{
              width: "100%",
              padding: "10px",
              borderRadius: "5px",
              border: "none",
              background: "#4CAF50",
              color: "#fff",
              fontSize: "16px",
              cursor: "pointer",
              transition: "background 0.3s ease",
            }}
          >
            Submit
          </button>
        </form>
      </div>
    </div>
  );
}
