import React, { useState, useEffect } from "react";
import axios from "axios";

export default function Adminhome() {
  const [image, setImage] = useState(null);
  const [price, setPrice] = useState("");
  const [productName, setProductName] = useState("");
  const [productId, setProductId] = useState("");
  const [products, setProducts] = useState([]); // New state for table data

  useEffect(() => {
    // Fetch the next productId from the server when the component mounts
    const fetchProductId = async () => {
      try {
        const response = await axios.get("http://localhost:5000/nextProductId");
        setProductId(response.data.productId);
      } catch (error) {
        console.error("Error fetching product ID:", error);
        alert("Failed to fetch product ID");
      }
    };

    fetchProductId();

    // Fetch existing products from the database
    const fetchProducts = async () => {
      try {
        const response = await axios.get("http://localhost:5000/products");
        setProducts(response.data); // Assuming the server returns an array of products
      } catch (error) {
        console.error("Error fetching products:", error);
        alert("Failed to fetch products");
      }
    };

    fetchProducts();
  }, []);

  const handleChange = (e, setter) =>
    setter(e.target.type === "file" ? URL.createObjectURL(e.target.files[0]) : e.target.value);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!image || !price || !productName) {
      return alert("Please fill all fields.");
    }

    const formData = new FormData();
    formData.append("image", e.target.image.files[0]);
    formData.append("price", price);
    formData.append("productName", productName);

    try {
      const response = await axios.post("http://localhost:5000/upload", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      alert(`Product ID: ${response.data.productId}\nImage: ${response.data.imageUrl}\nPrice: ${price}\nProduct Name: ${productName}`);

      // Fetch updated product list
      const updatedProducts = await axios.get("http://localhost:5000/products");
      setProducts(updatedProducts.data);
    } catch (error) {
      console.error("Error uploading image:", error);
      alert("Failed to upload image");
    }
  };

  return (
    <div>
      {/* Existing form code */}
      <form onSubmit={handleSubmit}>
        <div>
          <input
            type="text"
            value={productId}
            readOnly
            placeholder="Product ID"
            style={{ marginBottom: "10px", cursor: "not-allowed", backgroundColor: "#f0f0f0" }}
          />
        </div>
        <div>
          <input
            type="file"
            accept="image/*"
            name="image"
            onChange={(e) => handleChange(e, setImage)}
          />
          {image && <img src={image} alt="Selected" style={{ width: 100, height: 100, marginTop: 10 }} />}
        </div>
        <div>
          <input
            type="text"
            value={price}
            onChange={(e) => handleChange(e, setPrice)}
            placeholder="Enter price (number or text)"
          />
        </div>
        <div>
          <input
            type="text"
            value={productName}
            onChange={(e) => handleChange(e, setProductName)}
            placeholder="Enter product name"
          />
        </div>
        <button type="submit">Submit</button>
      </form>

      {/* Add some spacing */}
      <div style={{ marginTop: "50px" }}>
        <h2>Product List</h2>
        {products.length === 0 ? (
          <p>No products available</p>
        ) : (
          <table border="1" cellPadding="10" cellSpacing="0" style={{ width: "100%", textAlign: "left" }}>
            <thead>
              <tr>
                <th>Product ID</th>
                <th>Product Name</th>
                <th>Price</th>
                <th>Image</th>
              </tr>
            </thead>
            <tbody>
              {products.map((product) => (
                <tr key={product.productId}>
                  <td>{product.productId}</td>
                  <td>{product.productName}</td>
                  <td>{product.price}</td>
                  <td>
                    <img
                      src={product.imageUrl}
                      alt={product.productName}
                      style={{ width: "100px", height: "100px" }}
                    />
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}
