import React, { useState } from "react";
import { useHistory } from "react-router-dom";

export default function ADMINLOGIN() {
  const history = useHistory(); // To navigate after login
  const [name, setName] = useState("");
  const [gmail, setGmail] = useState("");
  const [password, setPassword] = useState("");
  const [nameTouched, setNameTouched] = useState(false);
  const [gmailTouched, setGmailTouched] = useState(false);
  const [passwordTouched, setPasswordTouched] = useState(false);

  const isNameValid = /^[A-Za-z\s]+$/.test(name) && name.length >= 3;
  const isGmailValid = gmail.includes("@gmail.com");
  const isPasswordValid = password.length > 4;

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!isNameValid || !isGmailValid || !isPasswordValid) {
      alert("Please fill out all fields correctly.");
      return;
    }

    try {
      const response = await fetch("http://localhost:5000/adminlogin", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name, gmail, password }),
      });

      const data = await response.json();
      if (response.ok) {
        alert("Login successful");
        history.push("/Adminhome"); // Navigate to AdminHome on success
      } else {
        alert(data.message);
      }
    } catch (error) {
      console.error("Error:", error);
      alert("An error occurred. Please try again later.");
    }
  };

  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "100vh",
        background: "linear-gradient(90deg, #000000, #222222)",
      }}
    >
      <div
        style={{
          width: "400px",
          padding: "20px",
          borderRadius: "10px",
          backgroundColor: "#333",
          color: "white",
          boxShadow: "0 0 20px rgba(0, 255, 0, 0.5)",
          border: "2px solid rgba(0, 255, 0, 0.8)",
        }}
      >
        <h1 style={{ textAlign: "center", color: "#0f0", marginBottom: "20px" }}>
          ADMIN LOGIN
        </h1>
        <form onSubmit={handleSubmit}>
          {/* Form inputs for Name, Gmail, Password */}
          <button
            type="submit"
            disabled={!isNameValid || !isGmailValid || !isPasswordValid}
            style={{
              width: "100%",
              padding: "10px",
              borderRadius: "5px",
              border: "none",
              backgroundColor: "#0f0",
              color: "black",
              fontWeight: "bold",
              cursor: "pointer",
            }}
          >
            Submit
          </button>
        </form>
      </div>
    </div>
  );
}
