import React, { useState } from "react";

export default function Adminhome() {
  const [image, setImage] = useState(null);
  const [price, setPrice] = useState("");
  
  const handleChange = (e, setter) => setter(e.target.type === "file" ? URL.createObjectURL(e.target.files[0]) : e.target.value);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!image || !price) return alert("Please fill all fields.");
    alert(`Image: ${image}\nPrice: ${price}`);
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <div>
          <input type="file" accept="image/*" onChange={(e) => handleChange(e, setImage)} />
          {image && <img src={image} alt="Selected" style={{ width: 100, height: 100, marginTop: 10 }} />}
        </div>
        <div>
          <input
            type="text"
            value={price}
            onChange={(e) => handleChange(e, setPrice)}
            placeholder="Enter price (number or text)"
          />
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}
