import React, { useState } from "react";
import { useNavigate } from "react-router-dom"; // Import useNavigate

export default function AdminLogin() {
  const [name, setName] = useState("");
  const [gmail, setGmail] = useState("");
  const [password, setPassword] = useState("");
  const [nameTouched, setNameTouched] = useState(false);
  const [gmailTouched, setGmailTouched] = useState(false);
  const [passwordTouched, setPasswordTouched] = useState(false);

  const navigate = useNavigate(); // Initialize navigate

  const isNameValid = /^[A-Za-z\s]+$/.test(name) && name.length >= 3;
  const isGmailValid = gmail.includes("@gmail.com");
  const isPasswordValid = password.length > 4;

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!isNameValid || !isGmailValid || !isPasswordValid) {
      alert("Please fill out all fields correctly.");
      return;
    }

    try {
      const response = await fetch("http://localhost:5000/adminlogin", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ name, gmail, password }),
      });

      const data = await response.json();
      if (response.ok) {
        alert("Login successful!");
        console.log("Submitted Data:", data);
        navigate("/adminhome"); // Navigate to AdminHome component
      } else {
        alert(data.message || "Error occurred while logging in.");
      }
    } catch (error) {
      console.error("Error submitting login:", error);
      alert("An error occurred. Please try again later.");
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.formContainer}>
        <h1 style={styles.heading}>ADMIN LOGIN</h1>
        <form onSubmit={handleSubmit}>
          <div style={styles.inputGroup}>
            <label style={styles.label}>Name</label>
            <input
              type="text"
              placeholder="Enter your name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              onFocus={() => setNameTouched(true)}
              style={{
                ...styles.input,
                borderColor: nameTouched && !isNameValid ? "red" : "#ccc",
              }}
            />
            {nameTouched && !isNameValid && (
              <span style={styles.error}>
                Enter a valid name (only letters and spaces, at least 3 characters)
              </span>
            )}
          </div>
          <div style={styles.inputGroup}>
            <label style={styles.label}>Gmail</label>
            <input
              type="text"
              placeholder="Enter your Gmail"
              value={gmail}
              onChange={(e) => setGmail(e.target.value)}
              onFocus={() => setGmailTouched(true)}
              style={{
                ...styles.input,
                borderColor: gmailTouched && !isGmailValid ? "red" : "#ccc",
              }}
            />
            {gmailTouched && !isGmailValid && (
              <span style={styles.error}>
                Enter a valid Gmail (must include @gmail.com)
              </span>
            )}
          </div>
          <div style={styles.inputGroup}>
            <label style={styles.label}>Password</label>
            <input
              type="password"
              placeholder="Enter your password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              onFocus={() => setPasswordTouched(true)}
              style={{
                ...styles.input,
                borderColor: passwordTouched && !isPasswordValid ? "red" : "#ccc",
              }}
            />
            {passwordTouched && !isPasswordValid && (
              <span style={styles.error}>
                Enter a valid password (at least 5 characters)
              </span>
            )}
          </div>
          <button
            type="submit"
            disabled={!isNameValid || !isGmailValid || !isPasswordValid}
            style={styles.button}
          >
            Submit
          </button>
        </form>
      </div>
    </div>
  );
}

const styles = {
  container: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    height: "100vh",
    backgroundColor: "rgba(0, 0, 255, 0.3)", // Transparent blue background
  },
  formContainer: {
    backgroundColor: "#fff",
    padding: "40px",
    borderRadius: "10px",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
    width: "400px",
    textAlign: "center",
  },
  heading: {
    marginBottom: "30px",
    fontFamily: "'Arial', sans-serif",
    color: "#333",
  },
  inputGroup: {
    marginBottom: "20px",
    textAlign: "left",
  },
  label: {
    display: "block",
    fontWeight: "bold",
    marginBottom: "5px",
    color: "#333",
  },
  input: {
    width: "100%",
    padding: "12px",
    borderRadius: "5px",
    border: "2px solid #ccc",
    boxSizing: "border-box",
    marginBottom: "5px",
    fontSize: "16px",
    transition: "all 0.3s ease",
  },
  error: {
    color: "red",
    fontSize: "12px",
    marginTop: "5px",
  },
  button: {
    backgroundColor: "#ff4d4d",
    color: "#fff",
    padding: "12px",
    borderRadius: "5px",
    width: "100%",
    fontSize: "16px",
    cursor: "pointer",
    border: "none",
    transition: "all 0.3s ease",
  },
};
