import React, { useState } from "react";

export default function AdminRegister() {
  const [name, setName] = useState("");
  const [gmail, setGmail] = useState("");
  const [password, setPassword] = useState("");
  const [nameTouched, setNameTouched] = useState(false);
  const [gmailTouched, setGmailTouched] = useState(false);
  const [passwordTouched, setPasswordTouched] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");

  const isNameValid = /^[A-Za-z\s]+$/.test(name) && name.length >= 3;
  const isGmailValid = gmail.includes("@gmail.com");
  const isPasswordValid = password.length > 4;

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!isNameValid || !isGmailValid || !isPasswordValid) {
      alert("Please fill out all fields correctly.");
      return;
    }

    try {
      const response = await fetch("http://localhost:5000/adminregister", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ name, gmail, password }),
      });

      const data = await response.json();
      if (data.error) {
        setErrorMessage(data.error);
      } else {
        alert("Form submitted successfully!");
        setName("");
        setGmail("");
        setPassword("");
      }
    } catch (error) {
      console.error("Error submitting form:", error);
      alert("There was an error submitting your form. Please try again.");
    }
  };

  return (
    <div>
      <h1>ADMIN REGISTER</h1>
      <form onSubmit={handleSubmit}>
        {/* Name Field */}
        <div>
          <label>Name</label>
          <input
            type="text"
            placeholder="Enter your name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            onFocus={() => setNameTouched(true)}
          />
          {nameTouched && !isNameValid && (
            <span>Enter a valid name (only letters and spaces, at least 3 characters)</span>
          )}
        </div>

        {/* Gmail Field */}
        <div>
          <label>Gmail</label>
          <input
            type="text"
            placeholder="Enter your Gmail"
            value={gmail}
            onChange={(e) => setGmail(e.target.value)}
            onFocus={() => setGmailTouched(true)}
          />
          {gmailTouched && !isGmailValid && (
            <span>Enter a valid Gmail (must include @gmail.com)</span>
          )}
        </div>

        {/* Password Field */}
        <div>
          <label>Password</label>
          <input
            type="password"
            placeholder="Enter your password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            onFocus={() => setPasswordTouched(true)}
          />
          {passwordTouched && !isPasswordValid && (
            <span>Enter a valid password (at least 5 characters)</span>
          )}
        </div>

        <button
          type="submit"
          disabled={!isNameValid || !isGmailValid || !isPasswordValid}
        >
          Submit
        </button>
      </form>

      {errorMessage && <p>{errorMessage}</p>}
    </div>
  );
}
