export default function Adminhome() {
  const [image, setImage] = useState(null);
  const [price, setPrice] = useState("");
  const [productName, setProductName] = useState("");
  const [productId, setProductId] = useState("");
  const [products, setProducts] = useState([]);
  const [editingId, setEditingId] = useState(null); // Track the product being edited

  // Fetch product data and the next productId on component mount
  useEffect(() => {
    const fetchInitialData = async () => {
      try {
        const productIdResponse = await axios.get("http://localhost:5000/nextProductId");
        setProductId(productIdResponse.data.productId);

        const productsResponse = await axios.get("http://localhost:5000/products");
        setProducts(productsResponse.data);
      } catch (error) {
        console.error("Error fetching data:", error);
        alert("Failed to fetch initial data");
      }
    };

    fetchInitialData();
  }, []);

  // Handle form input changes
  const handleChange = (e, setter) =>
    setter(e.target.type === "file" ? URL.createObjectURL(e.target.files[0]) : e.target.value);

  // Handle form submission to add a new product
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!image || !price || !productName) {
      return alert("Please fill all fields.");
    }

    const formData = new FormData();
    formData.append("image", e.target.image.files[0]);
    formData.append("price", price);
    formData.append("productName", productName);

    try {
      await axios.post("http://localhost:5000/upload", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      alert("Product added successfully!");

      // Refresh product list
      const updatedProducts = await axios.get("http://localhost:5000/products");
      setProducts(updatedProducts.data);

      // Reset form fields
      setImage(null);
      setPrice("");
      setProductName("");
    } catch (error) {
      console.error("Error adding product:", error);
      alert("Failed to add product");
    }
  };

  // Handle edit and update of a product
  const handleEdit = (product) => {
    setEditingId(product.productId);
    setProductName(product.productName);
    setPrice(product.price);
    setImage(product.imageUrl);
  };

  const handleUpdate = async (id) => {
    const formData = new FormData();
    formData.append("productName", productName);
    formData.append("price", price);
    if (image && image instanceof File) {
      formData.append("image", image);
    }

    try {
      await axios.put(`http://localhost:5000/products/${id}`, formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      alert("Product updated successfully!");

      // Refresh the product list
      const updatedProducts = await axios.get("http://localhost:5000/products");
      setProducts(updatedProducts.data);
      setEditingId(null); // Exit edit mode
    } catch (error) {
      console.error("Error updating product:", error);
      alert("Failed to update product");
    }
  };

  // Handle deletion of a product
  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/products/${id}`);
      alert("Product and image deleted successfully!");

      // Refresh the product list
      const updatedProducts = await axios.get("http://localhost:5000/products");
      setProducts(updatedProducts.data);
    } catch (error) {
      console.error("Error deleting product:", error);
      alert("Failed to delete product");
    }
  };

  return (
    <div>
      {/* Product Form */}
      <form onSubmit={handleSubmit}>
        <div>
          <input
            type="text"
            value={productId}
            readOnly
            placeholder="Product ID"
            style={{ marginBottom: "10px", cursor: "not-allowed", backgroundColor: "#f0f0f0" }}
          />
        </div>
        <div>
          <input
            type="file"
            accept="image/*"
            name="image"
            onChange={(e) => handleChange(e, setImage)}
            disabled={editingId !== null} // Disable image input if editing
          />
          {image && <img src={image} alt="Selected" style={{ width: 100, height: 100, marginTop: 10 }} />}
        </div>
        <div>
          <input
            type="text"
            value={price}
            onChange={(e) => handleChange(e, setPrice)}
            placeholder="Enter price"
            disabled={editingId !== null} // Disable price input if editing
          />
        </div>
        <div>
          <input
            type="text"
            value={productName}
            onChange={(e) => handleChange(e, setProductName)}
            placeholder="Enter product name"
            disabled={editingId !== null} // Disable product name input if editing
          />
        </div>
        <button type="submit" disabled={editingId !== null}>Submit</button> {/* Disable submit if editing */}
      </form>

      {/* Product Table */}
      <div style={{ marginTop: "50px" }}>
        <h2>Product List</h2>
        {products.length === 0 ? (
          <p>No products available</p>
        ) : (
          <table border="1" cellPadding="10" cellSpacing="0" style={{ width: "100%", textAlign: "left" }}>
            <thead>
              <tr>
                <th>Product ID</th>
                <th>Product Name</th>
                <th>Price</th>
                <th>Image</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {products.map((product) => (
                <tr key={product.productId}>
                  <td>{product.productId}</td>
                  <td>
                    {editingId === product.productId ? (
                      <input
                        type="text"
                        value={productName}
                        onChange={(e) => setProductName(e.target.value)}
                      />
                    ) : (
                      product.productName
                    )}
                  </td>
                  <td>
                    {editingId === product.productId ? (
                      <input
                        type="text"
                        value={price}
                        onChange={(e) => setPrice(e.target.value)}
                      />
                    ) : (
                      product.price
                    )}
                  </td>
                  <td>
                    {editingId === product.productId ? (
                      <input
                        type="file"
                        accept="image/*"
                        onChange={(e) => setImage(e.target.files[0])}
                      />
                    ) : (
                      <img
                        src={product.imageUrl}
                        alt={product.productName}
                        style={{ width: "100px", height: "100px" }}
                      />
                    )}
                  </td>
                  <td>
                    {editingId === product.productId ? (
                      <button onClick={() => handleUpdate(product.productId)}>Save</button>
                    ) : (
                      <button onClick={() => handleEdit(product)}>Edit</button>
                    )}
                    <button onClick={() => handleDelete(product.productId)}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}
