import React from "react";

function Mainpage({ data }) {
  return (
    <div>
      <h2>Main Page</h2>
      {data.length > 0 ? (
        <ul>
          {data.map((item, index) => (
            <li key={index}>{item}</li>
          ))}
        </ul>
      ) : (
        <p>No data available</p>
      )}
    </div>
  );
}

export default Mainpage;
