import React, { useState } from "react";

export default function AdminResister() {
  const [name, setName] = useState("");
  const [gmail, setGmail] = useState("");
  const [password, setPassword] = useState("");
  const [phone, setPhone] = useState("");
  const [nameTouched, setNameTouched] = useState(false);
  const [gmailTouched, setGmailTouched] = useState(false);
  const [passwordTouched, setPasswordTouched] = useState(false);
  const [phoneTouched, setPhoneTouched] = useState(false);

  const isNameValid = /^[A-Za-z\s]+$/.test(name) && name.length >= 3; // Regex to allow only letters and spaces
  const isGmailValid = gmail.includes("@gmail.com");
  const isPasswordValid = password.length > 4; // Updated condition for password validation
  const isPhoneValid = /^\d{10}$/.test(phone); // Regex to ensure exactly 10 numeric digits

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!isNameValid || !isGmailValid || !isPasswordValid || !isPhoneValid) {
      alert("Please fill out all fields correctly.");
      return;
    }
    alert(`Form submitted successfully!`);
    console.log({ name, gmail, password, phone });
    window.location.reload(); // Refresh the page after successful submission
  };

  const nameChange = (e) => {
    setName(e.target.value);
  };

  const gmailChange = (e) => {
    setGmail(e.target.value);
  };

  const passwordChange = (e) => {
    setPassword(e.target.value);
  };

  const phoneChange = (e) => {
    const value = e.target.value;
    if (/^\d{0,10}$/.test(value)) {
      setPhone(value);
    }
  };

  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "100vh",
        background: "linear-gradient(to right, #434343, #000000)",
      }}
    >
      <div
        style={{
          width: "400px",
          padding: "20px",
          borderRadius: "50%",
          background: "#fff",
          boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.25)",
          textAlign: "center",
        }}
      >
        <h1 style={{ marginBottom: "20px", color: "#333" }}>ADMIN REGISTER</h1>
        <form onSubmit={handleSubmit}>
          <div style={{ marginBottom: "15px" }}>
            <label htmlFor="name" style={{ display: "block", marginBottom: "5px" }}>Name:</label>
            <input
              id="name"
              type="text"
              placeholder="Enter your name"
              value={name}
              onChange={nameChange}
              onFocus={() => setNameTouched(true)}
              style={{
                width: "100%",
                padding: "10px",
                borderRadius: "5px",
                border: "1px solid #ccc",
              }}
            />
            {nameTouched && !isNameValid && (
              <span style={{ color: "red", fontSize: "12px" }}>
                Enter a valid name (only letters and spaces, at least 3 characters)
              </span>
            )}
          </div>
          <div style={{ marginBottom: "15px" }}>
            <label htmlFor="phone" style={{ display: "block", marginBottom: "5px" }}>Phone Number:</label>
            <input
              id="phone"
              type="text"
              placeholder="Enter your phone number"
              value={phone}
              onChange={phoneChange}
              onFocus={() => setPhoneTouched(true)}
              style={{
                width: "100%",
                padding: "10px",
                borderRadius: "5px",
                border: "1px solid #ccc",
              }}
            />
            {phoneTouched && !isPhoneValid && (
              <span style={{ color: "red", fontSize: "12px" }}>
                Enter a valid phone number (exactly 10 digits)
              </span>
            )}
          </div>
          <div style={{ marginBottom: "15px" }}>
            <label htmlFor="gmail" style={{ display: "block", marginBottom: "5px" }}>Gmail:</label>
            <input
              id="gmail"
              type="text"
              placeholder="Enter your Gmail"
              value={gmail}
              onChange={gmailChange}
              onFocus={() => setGmailTouched(true)}
              style={{
                width: "100%",
                padding: "10px",
                borderRadius: "5px",
                border: "1px solid #ccc",
              }}
            />
            {gmailTouched && !isGmailValid && (
              <span style={{ color: "red", fontSize: "12px" }}>
                Enter a valid Gmail (must include @gmail.com)
              </span>
            )}
          </div>
          <div style={{ marginBottom: "20px" }}>
            <label htmlFor="password" style={{ display: "block", marginBottom: "5px" }}>Password:</label>
            <input
              id="password"
              type="password"
              placeholder="Enter your password"
              value={password}
              onChange={passwordChange}
              onFocus={() => setPasswordTouched(true)}
              autoComplete="off"
              style={{
                width: "100%",
                padding: "10px",
                borderRadius: "5px",
                border: "1px solid #ccc",
              }}
            />
            {passwordTouched && !isPasswordValid && (
              <span style={{ color: "red", fontSize: "12px" }}>
                Enter the password (at least 5 characters)
              </span>
            )}
          </div>
          <button
            type="submit"
            disabled={
              !isNameValid || !isGmailValid || !isPasswordValid || !isPhoneValid
            }
            style={{
              width: "100%",
              padding: "10px",
              borderRadius: "5px",
              border: "none",
              background: "linear-gradient(to right, #434343, #000000)",
              color: "#fff",
              fontWeight: "bold",
              cursor: "pointer",
            }}
          >
            Submit
          </button>
        </form>
      </div>
    </div>
  );
}
