  const [name, setName] = useState("");
  const [gmail, setGmail] = useState("");
  const [password, setPassword] = useState("");
  const [phone, setPhone] = useState("");
  const [nameTouched, setNameTouched] = useState(false);
  const [gmailTouched, setGmailTouched] = useState(false);
  const [passwordTouched, setPasswordTouched] = useState(false);
  const [phoneTouched, setPhoneTouched] = useState(false);

  const isNameValid = /^[A-Za-z\s]+$/.test(name) && name.length >= 3;
  const isGmailValid = gmail.includes("@gmail.com");
  const isPasswordValid = password.length > 4;
  const isPhoneValid = phone.length >= 10;

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!isNameValid || !isGmailValid || !isPasswordValid || !isPhoneValid) {
      alert("Please fill out all fields correctly.");
      return;
    }
    alert(`Form submitted successfully!`);
    console.log({ name, gmail, password, phone });
    window.location.reload();
  };

  const nameChange = (e) => setName(e.target.value);
  const gmailChange = (e) => setGmail(e.target.value);
  const passwordChange = (e) => setPassword(e.target.value);

  return (
    <div
      style={{
        height: "100vh",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        background: "linear-gradient(120deg, #3a3d40, #18191a)",
        position: "relative",
        overflow: "hidden",
      }}
    >
      {/* Floating background circles */}
      <div
        style={{
          position: "absolute",
          width: "200px",
          height: "200px",
          background: "linear-gradient(to bottom, #ff7b00, #ff4500)",
          borderRadius: "50%",
          top: "10%",
          left: "15%",
          zIndex: 0,
          filter: "blur(100px)",
        }}
      ></div>
      <div
        style={{
          position: "absolute",
          width: "150px",
          height: "150px",
          background: "linear-gradient(to bottom, #ffdd00, #ff7b00)",
          borderRadius: "50%",
          bottom: "15%",
          right: "20%",
          zIndex: 0,
          filter: "blur(100px)",
        }}
      ></div>

      {/* Glass effect container */}
      <form
        onSubmit={handleSubmit}
        style={{
          zIndex: 1,
          width: "350px",
          padding: "30px",
          background: "rgba(255, 255, 255, 0.15)",
          borderRadius: "15px",
          backdropFilter: "blur(10px)",
          WebkitBackdropFilter: "blur(10px)",
          border: "1px solid rgba(255, 255, 255, 0.3)",
          boxShadow: "0 4px 30px rgba(0, 0, 0, 0.5)",
          textAlign: "center",
        }}
      >
        <h1
          style={{
            marginBottom: "20px",
            fontSize: "24px",
            color: "#ffffff",
            letterSpacing: "2px",
          }}
        >
          ADMIN REGISTER
        </h1>

        {/* Name Field */}
        <div style={{ marginBottom: "15px" }}>
          <input
            type="text"
            placeholder="Enter your name"
            value={name}
            onChange={nameChange}
            onFocus={() => setNameTouched(true)}
            style={{
              width: "100%",
              padding: "10px",
              borderRadius: "5px",
              border: "none",
              background: "rgba(255, 255, 255, 0.2)",
              color: "#fff",
              outline: "none",
              fontSize: "14px",
            }}
          />
          {nameTouched && !isNameValid && (
            <span style={{ color: "red", fontSize: "12px" }}>
              Enter a valid name (at least 3 characters)
            </span>
          )}
        </div>

        {/* Phone Field */}
        <div style={{ marginBottom: "15px" }}>
          <PhoneInput
            country={"in"}
            value={phone}
            onChange={(phone) => setPhone(phone)}
            onFocus={() => setPhoneTouched(true)}
            inputStyle={{
              width: "100%",
              padding: "10px",
              borderRadius: "5px",
              background: "rgba(255, 255, 255, 0.2)",
              border: "none",
              color: "#fff",
              fontSize: "14px",
            }}
            buttonStyle={{
              background: "rgba(255, 255, 255, 0.2)",
              border: "none",
            }}
          />
          {phoneTouched && !isPhoneValid && (
            <span style={{ color: "red", fontSize: "12px" }}>
              Enter a valid phone number
            </span>
          )}
        </div>

        {/* Gmail Field */}
        <div style={{ marginBottom: "15px" }}>
          <input
            type="text"
            placeholder="Enter your Gmail"
            value={gmail}
            onChange={gmailChange}
            onFocus={() => setGmailTouched(true)}
            style={{
              width: "100%",
              padding: "10px",
              borderRadius: "5px",
              border: "none",
              background: "rgba(255, 255, 255, 0.2)",
              color: "#fff",
              outline: "none",
              fontSize: "14px",
            }}
          />
          {gmailTouched && !isGmailValid && (
            <span style={{ color: "red", fontSize: "12px" }}>
              Enter a valid Gmail
            </span>
          )}
        </div>

        {/* Password Field */}
        <div style={{ marginBottom: "20px" }}>
          <input
            type="password"
            placeholder="Enter your password"
            value={password}
            onChange={passwordChange}
            onFocus={() => setPasswordTouched(true)}
            autoComplete="off"
            style={{
              width: "100%",
              padding: "10px",
              borderRadius: "5px",
              border: "none",
              background: "rgba(255, 255, 255, 0.2)",
              color: "#fff",
              outline: "none",
              fontSize: "14px",
            }}
          />
          {passwordTouched && !isPasswordValid && (
            <span style={{ color: "red", fontSize: "12px" }}>
              Enter a valid password
            </span>
          )}
        </div>

        {/* Submit Button */}
        <button
          type="submit"
          disabled={
            !isNameValid || !isGmailValid || !isPasswordValid || !isPhoneValid
          }
          style={{
            padding: "10px",
            borderRadius: "5px",
            border: "none",
            background:
              !isNameValid || !isGmailValid || !isPasswordValid || !isPhoneValid
                ? "gray"
                : "#4CAF50",
            color: "white",
            cursor:
              !isNameValid || !isGmailValid || !isPasswordValid || !isPhoneValid
                ? "not-allowed"
                : "pointer",
            fontSize: "14px",
            fontWeight: "bold",
            letterSpacing: "1px",
          }}
        >
          Submit
        </button>
      </form>
    </div>
  );
}
