import React, { useState } from "react";
import "./AdminResister.css"; // Add a separate CSS file for styling

export default function AdminResister() {
  const [name, setName] = useState("");
  const [gmail, setGmail] = useState("");
  const [password, setPassword] = useState("");
  const [phone, setPhone] = useState("");
  const [nameTouched, setNameTouched] = useState(false);
  const [gmailTouched, setGmailTouched] = useState(false);
  const [passwordTouched, setPasswordTouched] = useState(false);
  const [phoneTouched, setPhoneTouched] = useState(false);

  const isNameValid = /^[A-Za-z\s]+$/.test(name) && name.length >= 3; // Regex to allow only letters and spaces
  const isGmailValid = gmail.includes("@gmail.com");
  const isPasswordValid = password.length > 4; // Updated condition for password validation
  const isPhoneValid = /^\d{10}$/.test(phone); // Regex to ensure exactly 10 numeric digits

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!isNameValid || !isGmailValid || !isPasswordValid || !isPhoneValid) {
      alert("Please fill out all fields correctly.");
      return;
    }
    alert(`Form submitted successfully!`);
    console.log({ name, gmail, password, phone });
    window.location.reload(); // Refresh the page after successful submission
  };

  const nameChange = (e) => {
    setName(e.target.value);
  };

  const gmailChange = (e) => {
    setGmail(e.target.value);
  };

  const passwordChange = (e) => {
    setPassword(e.target.value);
  };

  const phoneChange = (e) => {
    const value = e.target.value;
    if (/^\d{0,10}$/.test(value)) {
      setPhone(value);
    }
  };

  return (
    <div className="admin-resister-container">
      <div className="admin-resister-form-wrapper">
        <h1 className="form-title">ADMIN REGISTER</h1>
        <form onSubmit={handleSubmit} className="admin-resister-form">
          <div className="form-group">
            <label htmlFor="name">Name:</label>
            <input
              id="name"
              type="text"
              placeholder="Enter your name"
              value={name}
              onChange={nameChange}
              onFocus={() => setNameTouched(true)}
              className="form-input"
            />
            {nameTouched && !isNameValid && (
              <span className="error-message">
                Enter a valid name (only letters and spaces, at least 3 characters)
              </span>
            )}
          </div>
          <div className="form-group">
            <label htmlFor="phone">Phone Number:</label>
            <input
              id="phone"
              type="text"
              placeholder="Enter your phone number"
              value={phone}
              onChange={phoneChange}
              onFocus={() => setPhoneTouched(true)}
              className="form-input"
            />
            {phoneTouched && !isPhoneValid && (
              <span className="error-message">
                Enter a valid phone number (exactly 10 digits)
              </span>
            )}
          </div>
          <div className="form-group">
            <label htmlFor="gmail">Gmail:</label>
            <input
              id="gmail"
              type="text"
              placeholder="Enter your Gmail"
              value={gmail}
              onChange={gmailChange}
              onFocus={() => setGmailTouched(true)}
              className="form-input"
            />
            {gmailTouched && !isGmailValid && (
              <span className="error-message">
                Enter a valid Gmail (must include @gmail.com)
              </span>
            )}
          </div>
          <div className="form-group">
            <label htmlFor="password">Password:</label>
            <input
              id="password"
              type="password"
              placeholder="Enter your password"
              value={password}
              onChange={passwordChange}
              onFocus={() => setPasswordTouched(true)}
              autoComplete="off"
              className="form-input"
            />
            {passwordTouched && !isPasswordValid && (
              <span className="error-message">
                Enter the password (at least 5 characters)
              </span>
            )}
          </div>
          <button
            type="submit"
            className="submit-button"
            disabled={
              !isNameValid || !isGmailValid || !isPasswordValid || !isPhoneValid
            }
          >
            Submit
          </button>
        </form>
      </div>
    </div>
  );
}
