import React, { useEffect, useState } from "react";

export default function Productpage() {
  const [products, setProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [filteredProducts, setFilteredProducts] = useState([]);

  // Fetch product data from the server
  useEffect(() => {
    fetch("http://localhost:5000/products")
      .then((response) => response.json())
      .then((data) => {
        setProducts(data);
        setFilteredProducts(data);
      })
      .catch((error) => console.error("Error fetching products:", error));
  }, []);

  const handleSearch = (e) => {
    const value = e.target.value;
    setSearchTerm(value);

    if (value === "") {
      setFilteredProducts(products); // Reset the product list
    } else {
      const filtered = products.filter((product) => {
        return (
          product.productName.toLowerCase().includes(value.toLowerCase()) ||
          product.price.toString().includes(value)
        );
      });
      setFilteredProducts(filtered);
    }
  };

  const handleAddToCart = (product) => {
    const formData = new FormData();
    formData.append("productName", product.productName);
    formData.append("price", product.price);

    if (product.imageUrl) {
      fetch(product.imageUrl)
        .then((res) => res.blob())
        .then((blob) => {
          const file = new File([blob], "image.jpg", { type: blob.type });
          formData.append("imageUrl", file);

          fetch("http://localhost:5000/addToCart", {
            method: "POST",
            body: formData,
          })
            .then((response) => response.json())
            .then((data) => {
              alert(data.message || `Added ${product.productName} to cart!`);
            })
            .catch((error) => console.error("Error adding to cart:", error));
        });
    }
  };

  const handleBuyNow = (product) => {
    alert(`Buying ${product.productName} for $${product.price}`);
  };

  return (
    <div>
      <h1>PRODUCT PAGE</h1>
      <div
        style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          margin: "20px",
          flexDirection: "column",
        }}
      >
        <div style={{ marginBottom: "10px" }}>
          <input
            type="text"
            placeholder="Search products by name or price..."
            value={searchTerm}
            onChange={handleSearch}
            style={{
              padding: "8px 12px",
              width: "300px",
              borderRadius: "15px",
              border: "1px solid #ccc",
            }}
          />
          <button
            onClick={() => setSearchTerm("")}
            style={{
              marginLeft: "10px",
              padding: "8px 15px",
              cursor: "pointer",
              backgroundColor: "green",
              color: "white",
              borderRadius: "50%",
              border: "none",
            }}
          >
            Clear
          </button>
        </div>
      </div>
      {filteredProducts.length > 0 ? (
        <div
          style={{
            display: "flex",
            flexWrap: "wrap",
            gap: "20px",
            padding: "10px",
          }}
        >
          {filteredProducts.map((product) => (
            <div
              key={product.productId}
              style={{
                minWidth: "200px",
                textAlign: "center",
                flex: "1 0 calc(14.28% - 20px)",
                maxWidth: "14.28%",
              }}
            >
              <h3>{product.productName}</h3>
              <p>Price: ${product.price}</p>
              <img
                src={product.imageUrl}
                alt={product.productName}
                style={{ width: "200px", height: "200px", objectFit: "cover" }}
              />
              <div style={{ marginTop: "10px" }}>
                <button
                  onClick={() => handleAddToCart(product)}
                  style={{
                    marginRight: "10px",
                    padding: "5px 10px",
                    cursor: "pointer",
                    backgroundColor: "#d90166",
                    borderRadius: "15px",
                  }}
                >
                  Add to Cart
                </button>
                <button
                  onClick={() => handleBuyNow(product)}
                  style={{
                    padding: "5px 10px",
                    cursor: "pointer",
                    backgroundColor: "#006400",
                    borderRadius: "15px",
                  }}
                >
                  Buy Now
                </button>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <p>No products available.</p>
      )}
    </div>
  );
}
