import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import Adminhome from "./Adminhome";
import Adminresister from "./Adminresister";
import Adminlogin from "./Adminlogin";
import Admingmail from "./Admingmail";
import Adminpassword from "./Adminpassword";
import Admindelete from "./Admindelete";
import Customerresister from "./Customerresister";
import Customerlogin from "./Customerlogin";
import Customergmail from "./Customergmail";
import Customerpassword from "./Customerpassword";
import Productpage from "./Productpage";
import Addtocartpage from "./Addtocartpage";
import Mainpage from "./Mainpage";

import "./App.css"; // Include for custom styling if needed

export default function App() {
  const [showAdminOptions, setShowAdminOptions] = useState(false);
  const [showCustomerOptions, setShowCustomerOptions] = useState(false);

  return (
    <Router>
      <div>
        {/* Navigation Bar */}
        <nav className="navbar">
          <h1 className="navbar-brand">Chandan Enterprises</h1>
          <div className="nav-buttons">
            <button
              className="nav-btn"
              onClick={() => {
                setShowAdminOptions(!showAdminOptions);
                setShowCustomerOptions(false);
              }}
            >
              Admin
            </button>
            {showAdminOptions && (
              <div className="dropdown">
                <Link to="/Adminresister" className="dropdown-item">Admin Register</Link>
                <Link to="/Adminlogin" className="dropdown-item">Admin Login</Link>
              </div>
            )}
            <button
              className="nav-btn"
              onClick={() => {
                setShowCustomerOptions(!showCustomerOptions);
                setShowAdminOptions(false);
              }}
            >
              Customer
            </button>
            {showCustomerOptions && (
              <div className="dropdown">
                <Link to="/Customerresister" className="dropdown-item">Customer Register</Link>
                <Link to="/Customerlogin" className="dropdown-item">Customer Login</Link>
              </div>
            )}
          </div>
        </nav>

        {/* Routes */}
        <Routes>
          <Route path="/Adminhome" element={<Adminhome />} />
          <Route path="/Adminresister" element={<Adminresister />} />
          <Route path="/Adminlogin" element={<Adminlogin />} />
          <Route path="/Admingmail" element={<Admingmail />} />
          <Route path="/Adminpassword" element={<Adminpassword />} />
          <Route path="/Admindelete" element={<Admindelete />} />
          <Route path="/Customerresister" element={<Customerresister />} />
          <Route path="/Customerlogin" element={<Customerlogin />} />
          <Route path="/Customergmail" element={<Customergmail />} />
          <Route path="/Customerpassword" element={<Customerpassword />} />
          <Route path="/Productpage" element={<Productpage />} />
          <Route path="/Addtocartpage" element={<Addtocartpage />} />
          <Route path="/" element={<Mainpage />} /> {/* Default route */}
        </Routes>
      </div>


    </Router>
  );
