import React, { useState } from "react";
import axios from "axios";

export default function Adminhome() {
  const [image, setImage] = useState(null);
  const [price, setPrice] = useState("");
  
  const handleChange = (e, setter) => setter(e.target.type === "file" ? URL.createObjectURL(e.target.files[0]) : e.target.value);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!image || !price) return alert("Please fill all fields.");

    const formData = new FormData();
    formData.append("image", e.target.image.files[0]);
    formData.append("price", price);

    try {
      const response = await axios.post("http://localhost:5000/upload", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      alert(`Image: ${response.data.imageUrl}\nPrice: ${price}`);
    } catch (error) {
      console.error("Error uploading image:", error);
      alert("Failed to upload image");
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <div>
          <input
            type="file"
            accept="image/*"
            name="image"
            onChange={(e) => handleChange(e, setImage)}
          />
          {image && <img src={image} alt="Selected" style={{ width: 100, height: 100, marginTop: 10 }} />}
        </div>
        <div>
          <input
            type="text"
            value={price}
            onChange={(e) => handleChange(e, setPrice)}
            placeholder="Enter price (number or text)"
          />
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}
