import React, { useState } from "react";

export default function Adminhome() {
  const [image, setImage] = useState(null);
  const [price, setPrice] = useState("");
  const [addedToCart, setAddedToCart] = useState(false);

  const handleChange = (e, setter) => setter(e.target.type === "file" ? URL.createObjectURL(e.target.files[0]) : e.target.value);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!image || !price) return alert("Please fill all fields.");
    alert(`Image: ${image}\nPrice: ${price}\nThe product is added to cart.`);
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <div>
          <input type="file" accept="image/*" onChange={(e) => handleChange(e, setImage)} />
          {image && <img src={image} alt="Selected" style={{ width: 100, height: 100, marginTop: 10 }} />}
        </div>
        <div>
          <input type="number" value={price} onChange={(e) => handleChange(e, setPrice)} placeholder="Enter price" />
        </div>
        <div>
          <button type="button" onClick={() => setAddedToCart(true)} disabled={!price || !image}>Add to Cart</button>
          {addedToCart && <div style={{ color: 'green', marginTop: 10 }}>The product is added to cart</div>}
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}
