// Client-Side Code
import React, { useEffect, useState } from "react";

export default function Productpage() {
  const [products, setProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [filteredProducts, setFilteredProducts] = useState([]);

  // Fetch product data from the server
  useEffect(() => {
    fetch("http://localhost:5000/products")
      .then((response) => response.json())
      .then((data) => {
        setProducts(data);
        setFilteredProducts(data);
      })
      .catch((error) => console.error("Error fetching products:", error));
  }, []);

  const handleSearch = () => {
    const filtered = products.filter((product) => {
      return (
        product.productName.toLowerCase().includes(searchTerm.toLowerCase()) ||
        product.price.toString().includes(searchTerm)
      );
    });
    setFilteredProducts(filtered);
  };

  const handleClearSearch = () => {
    setSearchTerm("");
    setFilteredProducts(products);
  };

  const handleAddToCart = (product) => {
    const formData = new FormData();
    formData.append("productName", product.productName);
    formData.append("price", product.price);

    if (product.imageUrl) {
      fetch(product.imageUrl)
        .then((res) => res.blob())
        .then((blob) => {
          const file = new File([blob], "image.jpg", { type: blob.type });
          formData.append("imageUrl", file);

          fetch("http://localhost:5000/addToCart", {
            method: "POST",
            body: formData,
          })
            .then((response) => response.json())
            .then((data) => {
              alert(data.message || `Added ${product.productName} to cart!`);
            })
            .catch((error) => console.error("Error adding to cart:", error));
        });
    }
  };

  const handleBuyNow = (product) => {
    alert(`Buying ${product.productName} for $${product.price}`);
  };

  return (
    <div>
      <h1>PRODUCT PAGE</h1>
      <div>
        <input
          type="text"
          placeholder="Search products by name or price..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button onClick={handleSearch}>Search</button>
        <button onClick={handleClearSearch} style={{ marginLeft: "10px" }}>
          Clear Search
        </button>
      </div>
      {filteredProducts.length > 0 ? (
        <div
          style={{
            display: "flex", flexWrap: "wrap", gap: "20px", padding: "10px",
          }}
        >
          {filteredProducts.map((product) => (
            <div
              key={product.productId}
              style={{
                minWidth: "200px", textAlign: "center", flex: "1 0 calc(14.28% - 20px)", maxWidth: "14.28%",
              }}
            >
              <h3>{product.productName}</h3>
              <p>Price: ${product.price}</p>
              <img
                src={product.imageUrl}
                alt={product.productName}
                style={{ width: "200px", height: "200px", objectFit: "cover" }}
              />
              <div style={{ marginTop: "10px" }}>
                <button
                  onClick={() => handleAddToCart(product)}
                  style={{
                    marginRight: "10px", padding: "5px 10px", cursor: "pointer",
                    backgroundColor: "#d90166", borderRadius: "15px",
                  }}
                >
                  Add to Cart
                </button>
                <button
                  onClick={() => handleBuyNow(product)}
                  style={{
                    padding: "5px 10px", cursor: "pointer", backgroundColor: "#006400",
                    borderRadius: "15px",
                  }}
                >
                  Buy Now
                </button>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <p>No products available.</p>
      )}
    </div>
  );
}
