// Productpage Component
import React, { useEffect, useState } from "react";

export default function Productpage() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    // Fetch data from the server
    fetch("http://localhost:5000/products")
      .then((response) => response.json())
      .then((data) => {
        // Sort the products by product ID in reverse order
        const sortedData = data.sort((a, b) => b.productId - a.productId);
        setProducts(sortedData);
      })
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  return (
    <div style={{ display: "flex", flexWrap: "wrap", gap: "20px", padding: "20px" }}>
      {products.map((product) => (
        <div key={product.productId} style={{ width: "200px", textAlign: "center", border: "1px solid #ddd", borderRadius: "10px", padding: "10px" }}>
          <img
            src={product.productImage}
            alt={product.productName}
            style={{ width: "100%", height: "auto", borderRadius: "8px" }}
          />
          <h3 style={{ margin: "10px 0" }}>{product.productName}</h3>
          <p style={{ fontWeight: "bold" }}>â‚¹ {product.productPrice}</p>
        </div>
      ))}
    </div>
  );
}
